import React from 'react';
import Draggable from 'react-draggable';
import Text from './ToggleableText';

class DragShape extends React.Component {

state = {
        shapeId:'',
        deltaPosition: {
          x: 0, y: 0
        },
      };

  handleDrag = (e, ui) => {
    console.log('shape moved');
    const {x, y} = this.state.deltaPosition;
    this.setState({
      deltaPosition: {
        x: x + ui.deltaX,
        y: y + ui.deltaY,
      }
    });

  };

  render() {
    const dragHandlers = {onStart: this.onStart, onStop: this.onStop};
    return (
      <Draggable
      key={shape.this.props.id}
      defaultPosition={this.props.defaultPosition}
      onDrag={this.handleDrag}
      bounds="parent" {...dragHandlers}

      >
            <div className={shapeClassName}>
              <Text
              id={shape.id}
              name={shape.name}
              changeShapeTextName = {this.props.changeShapeName}
              />
            </div>
      </Draggable>
    );
  }
}

export default DragShape;
