import React from 'react';
import Draggable from 'react-draggable';
import Text from './ToggleableText';

class DragPanel extends React.Component {

state = {
        shapes:[],
        currentDragShapeID:'',
        deltaPosition: {
          x: 0, y: 0
        },
      };

  handleDrag = (e, ui) => {
    const {x, y} = this.state.deltaPosition;
    this.setState({
      deltaPosition: {
        x: x + ui.deltaX,
        y: y + ui.deltaY,
      }
    });
  };


  savePosition = (id) => {
  //  console.log(id);
  this.props.handleShapePosition(id,this.state.deltaPosition.x, this.state.deltaPosition.y)
  }



  render() {
    const dragHandlers = {onStart: this.onStart, onStop: this.onStop};

    const draggableShapes = this.props.shapes.map( shape=>{
    //console.log(shape)
    const shapeClassName = 'box--' + shape.shape;
    //setup default position
    const position = {
      x: parseInt(shape.positionx),
      y: parseInt(shape.positiony)
    }
    const id = shape.id

      return(

        <Draggable
        key={shape.id}
        defaultPosition={position}
        onDrag={this.handleDrag}
        bounds="parent" {...dragHandlers}
        onMouseDown={this.savePosition({id})}
        >
              <div className={shapeClassName}>
                <Text
                id={shape.id}
                name={shape.name}
                changeShapeTextName = {this.props.changeShapeName}
                />
              </div>
        </Draggable>
      )
    })
    return (

        <div className="app__drag-panel">
        {draggableShapes}
        </div>

    );
  }
}

export default DragPanel;
