import React from 'react';
import Draggable from 'react-draggable';
import Text from './ToggleableText';

class DragPanel extends React.Component {

state = {
        deltaPosition: {
          x: 0, y: 0
        },
        controlledPosition: {
          x: -400, y: 200
        }
      };

  handleDrag = (e, ui) => {

    console.log(this.state.deltaPosition.x + "  " + this.state.deltaPosition.y )
  };


  onControlledDrag = (e, position) => {
    const {x, y} = position;
    this.setState({controlledPosition: {x, y}});
  };

  onControlledDragStop = (e, position) => {
    this.onControlledDrag(e, position);
    this.onStop();
  };

  render() {
    const dragHandlers = {onStart: this.onStart, onStop: this.onStop};

    const draggableShapes = this.props.shapes.map( shape=>{
    //console.log(shape)
    const shapeClassName = 'box--' + shape.shape;
    const position = {
      x: parseInt(shape.positionx),
      y: parseInt(shape.positiony)
    }

      return(
        <Draggable
        key={shape.id}
        defaultPosition={position}
        onDrag={this.handleDrag}
        bounds="parent" {...dragHandlers}>
              <div className={shapeClassName}>
                <Text
                id={shape.id}
                name={shape.name}
                changeShapeTextName = {this.props.changeShapeName}
                />
              </div>
        </Draggable>
      )
    })
    return (

        <div className="app__drag-panel">
        {draggableShapes}
        </div>

    );
  }
}

export default DragPanel;
